script:
frappe.ui.form.on("test", "len", function(frm) {
       cur_frm.set_value("area", (frm.doc.len* frm.doc.wid));
       });
frappe.ui.form.on("test", "wid", function(frm) {
       cur_frm.set_value("area", (frm.doc.len* frm.doc.wid))
       });

//izračunaj površino//
frappe.ui.form.on("test", "area", function(frm) {
cur_frm.set_value("mm2", (frm.doc.area* 100));
cur_frm.set_value("m2", (frm.doc.area/ 100));
if (frm.doc.area > 25) {cur_frm.set_value("areaovermin", (frm.doc.area-25))}
else {frm.set_value("areaovermin",00)}

       });

//izračunaj znesek glede na površino//
frappe.ui.form.on("test", "qty", function(frm) {

var d= (frm.doc.variable_price*frm.doc.areaovermin);
var q= (frm.doc.value);
if (frm.doc.area <= 25 && frm.doc.qty == 1){ frm.set_value("value", frm.doc.fixed_price);
} else if (frm.doc.area > 25 && frm.doc.qty > 1){frm.set_value("value",d+frm.doc.reduced_price);
}
cur_frm.set_value("valueovermin", d);

});

//izračunaj ceno glede na količino//
frappe.ui.form.on("test", "qty", function(frm) {
var qty= frm.doc.qty;
    if (qty < 2) {
        frm.set_value("reduced_price",frm.doc.fixed_price);
    } else if (qty <= 10) {
	frm.set_value("reduced_price",frm.doc.fixed_price*0.75);    
	} else if (qty <= 20) {
        frm.set_value("reduced_price",frm.doc.fixed_price*0.375);
    }else if (qty <= 50) {
       frm.set_value("reduced_price",frm.doc.fixed_price*0.325);
    }
else if (qty <= 100) {
        frm.set_value("reduced_price",frm.doc.fixed_price*0.275);
    }
else {
        msgprint("cena po dogovoru");
    }
frm.set_value("total_value",frm.doc.value*frm.doc.qty);
});
